const base64String = $input.first().json.body.data.message.base64;  // Adjust this path as needed
const mimeType = "image/jpeg";  // Change as necessary (e.g., audio/mp3)
const fileName = "output.jpg";  // Adjust the file extension

// Step 1: Validate the base64 string
if (!base64String || !base64String.match(/^[A-Za-z0-9+/=]+$/)) {
  throw new Error("Invalid base64 string detected");
}

// Step 2: Convert base64 to binary
try {
  // Decode the base64 string to binary
  const binaryData = Buffer.from(base64String, "base64");

  // Encode the binary data again to base64 for n8n
  const base64Binary = binaryData.toString("base64");

  return [
    {
      binary: {
        data: {
          mimeType: mimeType,
          fileName: fileName,
          data: base64Binary,
        },
      },
    },
  ];
} catch (error) {
  throw new Error("Error converting base64 to binary: " + error.message);
}

